CREATE VIEW sales.event_analysis_view AS
 SELECT range_agg(event_range) AS all_event_ranges
   FROM sales.events;

CREATE VIEW sales.event_analysis_view2 AS
 SELECT range_intersect_agg(event_range) AS overlapping_range
   FROM sales.events;

CREATE VIEW sales.employ_depart_view AS
 SELECT any_value(name) AS any_employee
   FROM public.employees;
